import React, { useContext, useEffect, useState } from 'react';
import './Status.css';
import { DataContext } from '../../DataContext/DataContext';

const Status = () => {
  const { data, ordering } = useContext(DataContext);
  const [sortedData, setSortedData] = useState([]);
  useEffect(() => {
    // Ensure data is valid and an array
   
    if (!Array.isArray(data)) return;

    const sortedArray = [...data]; // Create a copy of the data array

    if (ordering === 'title') {
      sortedArray.sort((a, b) => a.title.localeCompare(b.title)); // Sort alphabetically by title
    } else if (ordering === 'priority') {
      sortedArray.sort((a, b) => a.priority - b.priority); // Sort numerically by priority
      console.log()
    }
    setSortedData(sortedArray); // Update sorted data
  }, [ordering, data]);

  const status = ['Backlog', 'Todo', 'In progress', 'Done', 'Cancel'];

  // Display loading state while data is being fetched
  if (!Array.isArray(data) || data.length === 0) {
    return <div>Loading....</div>;
  }

  return (
    <div className='Heading'>
      {status.map((statusItem, index) => (
        <div className='status-column' key={statusItem}>
          <span>{statusItem}</span>
          <div>
            {sortedData
              .filter(item => item.status === statusItem) // Filter items by status
              .map(item => (
                <div key={item.id} className='status-card'> {/* Use a unique key */}
                  <p>{item.title}</p>
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Status;
