import React, { useContext, useEffect, useState } from 'react';
import './Status.css';
import { DataContext } from '../../DataContext/DataContext';

const Status = () => {
  const { data, ordering } = useContext(DataContext);
  const [sortedData, setSortedData] = useState([]);

  useEffect(() => {
    if (!data) return; // Ensure the hook logic doesn't run if data is null/undefined

    const sortedArray = [...data]; // Copy the data array

    if (ordering === 'title') {
      sortedArray.sort((a, b) => a.title.localeCompare(b.title)); // Sort by title alphabetically
    } else if (ordering === 'priority') {
      sortedArray.sort((a, b) => a.priority - b.priority); // Sort by priority (numerically)
    }

    setSortedData(sortedArray); // Update sorted data
  }, [ordering, data]); // Dependency array ensures sorting happens when ordering or data changes

  const status = ['Backlog', 'Todo', 'In progress', 'Done', 'Cancel'];

  // Render loading state inside the return statement
  if (!data) {
    return <div>Loading....</div>;
  }

  return (
    <div className='Heading'>
      {status.map((statusItem, index) => (
        <div className='status-column' key={index}>
          <span>{statusItem}</span>
          <div>
            {sortedData &&sortedData
              .filter(item => item.status === statusItem) // Filter items by status
              .map((item, idx) => (
                <div key={idx} className='status-card'>
                  <p>{item.title}</p>
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Status;
