import React, { useContext, useEffect } from 'react'
import "./Status.css"
import { DataContext } from '../../DataContext/DataContext'

const Status = () => {
  const {data,ordering} = useContext(DataContext);
  if(!data) return (<div>Loading....</div>)
  const status = ["Backlog","Todo","In progress","Done","Cancel"];
  
  useEffect(()=>{
    const sortedData = [...data];
    if(ordering==='title'){
      sortedData.sort((a,b)=> a.title.localeCompare(b.title));
    }
    else{
      sortedData.sort((a,b)=> a.priority-b.priority);
    }
  },[ordering])

  return (
    <div className='Heading'>
      {
        status.map((statusItem,index)=>(
          <div className="status-column">
            <span>{statusItem}</span>
            <div>
              {
                data.filter(item=>item.status===statusItem).map((item,idx)=>{
                  <div key={idx} className='status-card'>
                      <p>{item.title}</p>
                  </div>
                })
              }
            </div>
          </div>
        ))
      }
    </div>
  )
}

export default Status
