import React, { useContext, useEffect, useState } from 'react';
import './Status.css';
import { DataContext } from '../../DataContext/DataContext';

const Status = () => {
  const { data, ordering } = useContext(DataContext);
  const [sortedData, setSortedData] = useState([]);

  // Early return for loading state
  if (!data) {
    return <div>Loading....</div>;
  }
  setSortedData(data);
  const status = ['Backlog', 'Todo', 'In progress', 'Done', 'Cancel'];

  // Now we safely call useEffect after the early return, ensuring no hooks are conditional
 
  return (
    <div className='Heading'>
      {status.map((statusItem, index) => (
        <div className='status-column' key={index}>
          <span>{statusItem}</span>
          <div>
            {sortedData
              .filter(item => item.status === statusItem)
              .map((item, idx) => (
                <div key={idx} className='status-card'>
                  <p>{item.title}</p>
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Status;
